using System.Collections.Generic;using UnityEngine;using UnityEngine.EventSystems;using UnityEngine.UI;using USocket.Messages;using XLua;[Hotfix]public class Shop{    //商店类型    public enum SHOPTYPE    {        SHOPTYPE_NONE       = 0,        SHOPTYPE_COIN       = 1,           //金币商店        SHOPTYPE_DIAMOND    = 2,           //钻石商店        SHOPTYPE_EXCHANGE   = 3,           //兑换商店        SHOPTYPE_MAX,       }    //商店物品类型    public enum ITEMTYPE    {        ITEMTYPE_NONE           = 0,        ITEMTYPE_COIN           = 1,          //金币        ITEMTYPE_DIAMOND        = 2,          //钻石        ITEMTYPE_JINGDONG       = 3,          //京东购物卡        ITEMTYPE_MOBILE         = 4,          //手机充值卡
        ITEMTYPE_NEWHANDGIFT    = 5,          //新手礼包
        ITEMTYPE_BENEFITGIFT    = 6,          //特惠礼包
        ITEMTYPE_GOODS          = 7,          //实物
    }    //所需货币类型    public enum CURRENCYTYPE    {        CURRENCYTYPE_NONE   = 0,        CURRENCYTYPE_RMB    = 1,            //人民币        CURRENCYTYPE_DIAMOND= 2,            //钻石        CURRENCYTYPE_LOTTERY= 3,            //奖券
        CURRENCYTYPE_GOODS  = 4,            //兑换实物-物品ID
    }    //兑换界面类型    public enum EXCHANGEUITYPE    {        EXCHANGEUITYPE_NONE = 0,        EXCHANGEUITYPE_COIN = 1,             //兑换金币界面        EXCHANGEUITYPE_JINGDONG = 2,         //兑换京东卡界面        EXCHANGEUITYPE_MOBILE = 3,           //兑换手机充值卡界面        EXCHANGEUITYPE_RECORD =4,           //兑换记录界面    }    //兑换记录数据    private struct ExchangeRecordData    {       public string ExchangeOrderNo;     //兑换订单ID       public uint ExchangeItemId;      //兑换物品ID       public byte ExchangeOrderState;    //订单处理状态(0未处理，1已处理)       public  ExchangeRecordData(string orderNo,uint itemId,byte state)        {            ExchangeOrderNo = orderNo;            ExchangeItemId = itemId;            ExchangeOrderState = state;        }    }    //确认界面订单信息缓存    private struct OrderCacheData    {        public string PhoneNum;        public uint ItemId;        public string RecvName;        public string RecvAddress;        public void SetOrderCacheData(string phone,uint item,string name = null,string addr= null)        {            PhoneNum = phone;            ItemId = item;            RecvName = name;            RecvAddress = addr;        }    }    //商城主界面    private  GameObject ShopMainUI;    //金币商店界面    private  GameObject CoinShopUI;    //钻石商店界面    private  GameObject DiamondShopUI;    //兑换商店界面    private  GameObject ExchangeShopUI;    //金币商店物品perfab    private Object CoinItemPerfab;    //钻石商店物品perfab    private Object DiamondItemPerfab;    //兑换商店物品perfab    private Object ExchangeItemPerfab;       //购买结果提示界面    private GameObject BuyItemResultUI;    private Object BuyItemResultUIPerfab;    //支付平台选择界面    private GameObject PayPlatfromSelectUI;    private Object PayPlatfromSelectPerfab;    private Object PayPlatfromIconPerfab;    //各商店物品初始化标记    private bool[] ShopGoodsInitFlag;    //兑换记录数据初始化标记    private bool ExChangeRecordDataFlag;    //兑换记录prefab    private Object ExchangeRecordPrefab;    //兑换商店总开关(GM工具可设置显示与否)    public bool ExchangeShopOpenOnOff;    private List<ExchangeRecordData> RecordDataList;    OrderCacheData OrderConfirmCacheData;    public Shop(GameObject canvasuiObj)    {        RecordDataList = new List<ExchangeRecordData>();        OrderConfirmCacheData = new OrderCacheData();        ExchangeShopOpenOnOff = GameMain.hall_.GetPlayerData().ShopExchangeTurnOff;        ShopMainUI = null;        RegitserMsgHandle();    }    private void RegitserMsgHandle()    {        ////兑换记录数据        //CMsgDispatcher.GetInstance().RegMsgDictionary((uint)GameCity.EMSG_ENUM.CrazyCityMsg_BACKGETTRADEINFO        //    , HandleExchangeRecordDataMsg);        //商城兑换开关        CMsgDispatcher.GetInstance().RegMsgDictionary((uint)GameCity.EMSG_ENUM.CrazyCityMsg_SETLETTRORYCOVERT            , HandleExchangeTurnOnOff);    }    public void InitShopUI()    {        if (ShopMainUI != null)            return;        ShopGoodsInitFlag = new bool[(int)SHOPTYPE.SHOPTYPE_MAX];        for (int i = 0; i < (int)SHOPTYPE.SHOPTYPE_MAX; i++)            ShopGoodsInitFlag[i] = false;        ExChangeRecordDataFlag = false;        InitShopMainUI();        InitShopKindsUI();        InitShopUIBtnListenter();        InitShopPlayerMoneyText();        //初次打开默认显示金币界面        //ShowKindsShopUI(SHOPTYPE.SHOPTYPE_COIN);    }    public bool isShowActive()    {        if (ShopMainUI != null)            return ShopMainUI.activeSelf;        return false;    }    //初始商店主界面    private void InitShopMainUI()    {        if (ShopMainUI == null)        {            AssetBundle bundle = AssetBundleManager.GetAssetBundle(GameDefine.HallAssetbundleName);            if (bundle == null)                return;            Object obj0 = bundle.LoadAsset("Lobby_Shop_new") as Object;            ShopMainUI = GameMain.instantiate(obj0) as GameObject;            Transform CanvasUItf = GameObject.Find("Canvas/Root").transform;            ShopMainUI.transform.SetParent(CanvasUItf, false);            ShopMainUI.SetActive(false);            CoinItemPerfab = bundle.LoadAsset("Shop_goods_Coin") as Object;            DiamondItemPerfab = bundle.LoadAsset("Shop_Diamond") as Object;            ExchangeItemPerfab = bundle.LoadAsset("Shop_goods_Exchange") as Object;            BuyItemResultUIPerfab = bundle.LoadAsset("Tips_Buy") as Object;            PayPlatfromSelectPerfab = bundle.LoadAsset("Shop_Pay") as Object;            PayPlatfromIconPerfab = bundle.LoadAsset("Pay_Button") as Object;        }    }    //初始化商店子类商店界面    private void InitShopKindsUI()    {        if (ShopMainUI == null)            return;        //CoinShopUI = ShopMainUI.transform.FindChild("Bottom").FindChild("Shop_Coin").gameObject;        DiamondShopUI = ShopMainUI.transform.Find("Bottom").Find("Shop_Diamond").gameObject;        //ExchangeShopUI = ShopMainUI.transform.FindChild("Bottom").FindChild("Shop_Exchange").gameObject;        //InitCoinShopItem();        InitDiamondShopItem();        //InitExchangeShopItem();    }    //初始商店界面玩家金钱显示    private void InitShopPlayerMoneyText()    {        RefreshShopPlayerCoinText();        RefreshShopPlayerDiamondText();        RefreshShopPlayerLotteryText();        RefreshShopPlayerVipText();    }    //初始购买结果确认界面    private void InitBuyItemResultDialog()    {        if (BuyItemResultUI == null)            BuyItemResultUI = GameMain.instantiate(BuyItemResultUIPerfab) as GameObject;        GameObject canvasobj = GameObject.Find("Canvas/Root");        BuyItemResultUI.transform.SetParent(canvasobj.transform, false);        BuyItemResultUI.SetActive(false);    }    //初始化支付平台选择界面    private void InitPayPlatfromSelectUI()    {        if(PayPlatfromSelectUI == null)        {            PayPlatfromSelectUI = GameMain.instantiate(PayPlatfromSelectPerfab) as GameObject;        }        PayPlatfromSelectUI.transform.SetParent(ShopMainUI.transform, false);        Transform tf = PayPlatfromSelectUI.transform.Find("ImageBG").Find("Viewport_Pay").Find("Content_Pay");        AssetBundle bundle = AssetBundleManager.GetAssetBundle(GameDefine.HallAssetbundleName);        if (bundle == null)            return;        GameObject closebtn = PayPlatfromSelectUI.transform.Find("ImageBG").Find("ButtonClose").gameObject;        XPointEvent.AutoAddListener(closebtn, ClickSelectPayPlatfromCloseBtn, null);        for (int i = 1; i <= (int)PayPlatform.AliPay; i++)        {            if (GameMain.hall_.GetPlayer().IsPayPlatfromHavaFlag(i))            {                GameObject platfromicon = GameMain.instantiate(PayPlatfromIconPerfab) as GameObject;                platfromicon.transform.SetParent(tf, false);                Image img = platfromicon.GetComponent<Image>();                Sprite spr = bundle.LoadAsset("Icon_Pay_0" + i.ToString()) as Sprite;                img.sprite = spr;                XPointEvent.AutoAddListener(platfromicon, ClickPayPlatfromIcon, i);            }        }        PayPlatfromSelectUI.SetActive(false);    }    //打开购买结果提示界面    public void OpenBuyResultDialogUI(string itemIcon,ITEMTYPE itemtype,uint itemNum,uint tipsId)    {        if (BuyItemResultUI == null)            InitBuyItemResultDialog();        BuyItemResultUI.transform.SetAsLastSibling();        Image ItemIcon =  BuyItemResultUI.transform.Find("ImageBG").Find("ImageIcon")            .gameObject.GetComponent<Image>();        //Animator runanima = BuyItemResultUI.transform.FindChild("ImageBG").FindChild("ImageBG_01")        //   .gameObject.GetComponent<Animator>();        AssetBundle bundle = AssetBundleManager.GetAssetBundle(GameDefine.HallAssetbundleName);        if (bundle != null)        {            Sprite iconsprite = bundle.LoadAsset<Sprite>(itemIcon) as Sprite;            ItemIcon.sprite = iconsprite;            //Object animator = bundle.LoadAsset("Animation_ImageBG_01") as Object;            //runanima.runtimeAnimatorController = (RuntimeAnimatorController)animator;            //runanima.Play("Tips_Buy");            //runanima.SetBool("Play",true);            //AnimationClip ac = runanima.runtimeAnimatorController.animationClips[0];            //runanima.Stop();            //Debug.Log("new Animation_ImageBG_01.......:" + ac.length );        }               Text ResultText = BuyItemResultUI.transform.Find("ImageBG").Find("GroupText").Find("TextTop")            .gameObject.GetComponent<Text>();        Text ResultInfoText = BuyItemResultUI.transform.Find("ImageBG").Find("TextInfo")            .gameObject.GetComponent<Text>();        string itemtypeStr = "";        switch (itemtype)        {            case ITEMTYPE.ITEMTYPE_COIN:                itemtypeStr = "金币";                break;            case ITEMTYPE.ITEMTYPE_DIAMOND:                itemtypeStr = "钻石";                break;            case ITEMTYPE.ITEMTYPE_JINGDONG:                itemtypeStr = "京东购物卡";                break;            case ITEMTYPE.ITEMTYPE_MOBILE:                itemtypeStr = "手机充值卡";                break;            default:                break;        }        ResultInfoText.text = itemNum + itemtypeStr;        TipsData tipsdata = CCsvDataManager.Instance.TipsDataMgr.GetTipsData(tipsId);        if(tipsdata != null)        {            ResultText.text = tipsdata.TipsText;        }        BuyItemResultUI.SetActive(true);    }    //关闭购买结果提示框    private void CloseBuyResultDialogUI(EventTriggerType eventtype, object parma, PointerEventData eventData)    {        if(eventtype == EventTriggerType.PointerClick)        {            CustomAudio.GetInstance().PlayCustomAudio(1002);            BuyItemResultUI.SetActive(false);        }    }    //打开支付平台选择界面    public void OpenPayPlatfromSelectUI(uint itemid)    {        if (PayPlatfromSelectUI == null)            InitPayPlatfromSelectUI();               GameMain.hall_.GetPlayer().SetPlayerBuyingItemId(itemid);        PayPlatfromSelectUI.SetActive(true);               }    //关闭支付平台选择界面    private void ClosePayPlatfromSelectUI(bool isPlayerCancle = false)    {        PayPlatfromSelectUI.SetActive(false);    }    //点击关闭支付平台选择按钮    private void ClickSelectPayPlatfromCloseBtn(EventTriggerType eventtype, object parma, PointerEventData eventData)    {        if (eventtype == EventTriggerType.PointerClick)        {            CustomAudio.GetInstance().PlayCustomAudio(1002);            ClosePayPlatfromSelectUI(true);        }    }    //点击支付平台ICON    private void ClickPayPlatfromIcon(EventTriggerType eventtype, object parma, PointerEventData eventData)    {        if (eventtype == EventTriggerType.PointerClick)        {            CustomAudio.GetInstance().PlayCustomAudio(1002);            PayPlatform plat = (PayPlatform)parma;#if VVPAY_H5            GameMain.hall_.GetPlayer().RequestBuyItem(GameMain.hall_.GetPlayer().GetPlayerBuyingItemId(), plat+2);#else            GameMain.hall_.GetPlayer().RequestBuyItem(GameMain.hall_.GetPlayer().GetPlayerBuyingItemId(), plat);#endif            ClosePayPlatfromSelectUI();        }    }    //刷新商城界面玩家货币数值    public void RefreshPlayerCurrency()    {        if (isShowActive() == false)            return;        RefreshShopPlayerCoinText();        RefreshShopPlayerDiamondText();        RefreshShopPlayerLotteryText();    }    //刷新金币显示    public  void RefreshShopPlayerCoinText()    {        //Transform playercoin = ShopMainUI.transform.FindChild("Top").FindChild("Image_coinframe");        ////更新金币文本        //Text playercoinTxt = playercoin.FindChild("Text_Coin").gameObject.GetComponent<Text>();        //playercoinTxt.text = GameMain.hall_.GetPlayerData().GetCoin().ToString();    }    //刷新钻石显示    public  void RefreshShopPlayerDiamondText()    {        //Transform playerdiamond = ShopMainUI.transform.FindChild("Top").FindChild("Image_DiamondFrame");        ////更新钻石文本        //Text playerdiamon