ach (uint itemid in coinItemList)        {            ShopItemdata itemdata = CCsvDataManager.Instance.ShopItemDataMgr.GetShopItemData(itemid);            if (itemdata == null)                continue;            GameObject obj = GameMain.instantiate(CoinItemPerfab) as GameObject;            obj.transform.SetParent(GoodsShelves,false);            //设置商品价格            Text price = obj.transform.Find("ButtonBuy").Find("Text_Price").gameObject.GetComponent<Text>();            price.text = itemdata.ItemPrice.ToString();            //设置获取商品基本数量            GameObject basenumObj = obj.transform.Find("ImageBG").Find("ImageText").Find("Text_baseMoneyNum").gameObject;            basenumObj.GetComponent<Text>().text = itemdata.ItemNum.ToString();            //设置获取商品总数量            Text TotalnumText = obj.transform.Find("ImageBG").Find("ImageText").Find("Text_TotalMoneyNum").gameObject.GetComponent<Text>();            TotalnumText.text = (itemdata.ItemNum + itemdata.PresentItemNum).ToString()+"金币";            //设置商品ICON            Image Iconimg = obj.transform.Find("ImageBG").Find("ImageIcon").gameObject.GetComponent<Image>();            AssetBundle bundle = AssetBundleManager.GetAssetBundle(GameDefine.HallAssetbundleName);            if (bundle != null)            {                Sprite iconsprite = bundle.LoadAsset<Sprite>(itemdata.ItemIcon) as Sprite;                Iconimg.sprite = iconsprite;            }            //设置获取商品赠送百分比            GameObject ZengsongObj = obj.transform.Find("ImageBG").Find("ImageZengsong").gameObject;            int ratePercent = (int)(((float)itemdata.PresentItemNum / itemdata.ItemNum) * 100);            if (ratePercent == 0)            {                basenumObj.SetActive(false);                ZengsongObj.SetActive(false);            }            else            {                ZengsongObj.SetActive(true);                ZengsongObj.transform.Find("Text_GiveRate").gameObject.GetComponent<Text>().text = "+" + ratePercent.ToString() + "%";            }            //购买按钮点击事件            GameObject Buybtn = obj.transform.Find("ButtonBuy").gameObject;            XPointEvent.AutoAddListener(Buybtn, ClickBuyCoinBtn, itemdata.ItemID);        }        ShopGoodsInitFlag[(int)SHOPTYPE.SHOPTYPE_COIN] = true;    }    //初始钻石商店物品    private void InitDiamondShopItem()    {        if (ShopGoodsInitFlag[(int)SHOPTYPE.SHOPTYPE_DIAMOND])            return;        if (ShopMainUI == null)            return;        List<uint> diamondItemList = CCsvDataManager.Instance.ShopItemDataMgr.GetShopItemListByType(SHOPTYPE.SHOPTYPE_DIAMOND);        if (diamondItemList.Count == 0)            return;#if UNITY_IOS        //如果是IOS则需初始内购环境及商品 （审核版本）        if(Application.platform == RuntimePlatform.IPhonePlayer)        {            ApplePay.Instance.InitUnityPurchase(diamondItemList);        }#endif        Transform GoodsShelves = DiamondShopUI.transform.Find("Content_Shop_Diamond");        foreach (uint itemid in diamondItemList)        {            ShopItemdata itemdata = CCsvDataManager.Instance.ShopItemDataMgr.GetShopItemData(itemid);            if (itemdata == null)                continue;            GameObject obj = GameMain.instantiate(DiamondItemPerfab) as GameObject;            obj.transform.SetParent(GoodsShelves,false);            //设置商品价格            Text price = obj.transform.Find("ButtonBuy").Find("Text_Price").gameObject.GetComponent<Text>();            price.text = "￥" + itemdata.ItemPrice.ToString();            //设置获取商品基本数量            GameObject basenumObj = obj.transform.Find("ImageBG").Find("ImageText").Find("TextNum_BaseDiamondNum").gameObject;            basenumObj.GetComponent<Text>().text = itemdata.ItemNum.ToString();            //设置获取商品总数量            Text TotalnumText = obj.transform.Find("ImageBG").Find("ImageText").Find("TextNum_TotalDiamonNum").gameObject.GetComponent<Text>();            TotalnumText.text = (itemdata.ItemNum + itemdata.PresentItemNum).ToString()+"钻石";            //设置商品ICON            Image Iconimg = obj.transform.Find("ImageBG").Find("ImageIcon").gameObject.GetComponent<Image>();            AssetBundle bundle = AssetBundleManager.GetAssetBundle(GameDefine.HallAssetbundleName);            if(bundle != null)            {                Sprite iconsprite = bundle.LoadAsset<Sprite>(itemdata.ItemIcon) as Sprite;                Iconimg.sprite = iconsprite;            }            //设置获取商品赠送百分比            GameObject ZengsongObj = obj.transform.Find("ImageBG").Find("ImageZengsong").gameObject;            int ratePercent = (int)(((float)itemdata.PresentItemNum / itemdata.ItemNum) *100);            if (ratePercent == 0)            {                basenumObj.SetActive(false);                ZengsongObj.SetActive(false);            }            else            {                ZengsongObj.SetActive(true);                ZengsongObj.transform.Find("Text_GiveRate").gameObject.GetComponent<Text>().text = "+" + ratePercent.ToString() + "%";            }            //购买按钮点击事件            GameObject Buybtn = obj.transform.Find("ButtonBuy").gameObject;            XPointEvent.AutoAddListener(Buybtn,ClickBuyDiamondBtn, itemdata.ItemID);        }        ShopGoodsInitFlag[(int)SHOPTYPE.SHOPTYPE_DIAMOND] = true;    }    //初始奖券兑换商店物品    private void InitExchangeShopItem()    {        if (ShopMainUI == null)            return;        List<uint> exchaneItemList = CCsvDataManager.Instance.ShopItemDataMgr.GetShopItemListByType(SHOPTYPE.SHOPTYPE_EXCHANGE);        if (exchaneItemList.Count == 0)            return;        byte playerLv = GameMain.hall_.GetPlayerData().GetVipLevel();        long playerlottery = GameMain.hall_.GetPlayerData().GetLottery();        Transform GoodsShelves = ExchangeShopUI.transform.Find("ImageViewport/Content_Shop_Exchange");        if (ShopGoodsInitFlag[(int)SHOPTYPE.SHOPTYPE_EXCHANGE])        {            for (int i = 0; i < GoodsShelves.childCount; i++)            {                Object.Destroy(GoodsShelves.GetChild(i).gameObject);            }                   }        foreach (uint itemid in exchaneItemList)        {            ShopItemdata itemdata = CCsvDataManager.Instance.ShopItemDataMgr.GetShopItemData(itemid);            if (itemdata == null)                continue;                       if(itemdata.ItemType == ITEMTYPE.ITEMTYPE_JINGDONG || itemdata.ItemType == ITEMTYPE.ITEMTYPE_MOBILE)            {                //特殊时间段内都显示(22:00 -8:00)                if(System.DateTime.Now.Hour >=8 && System.DateTime.Now.Hour < 22)                {                    //只有VIP2 且有奖券才显示实物兑换                    if (playerLv < 2 || playerlottery < 200)                        continue;                }                          }            GameObject obj = GameMain.instantiate(ExchangeItemPerfab) as GameObject;            obj.transform.SetParent(GoodsShelves,false);            //设置商品ICON            Image Iconimg = obj.transform.Find("ImageBG").Find("ImageIcon").gameObject.GetComponent<Image>();            AssetBundle bundle = AssetBundleManager.GetAssetBundle(GameDefine.HallAssetbundleName);            if (bundle != null)            {                Sprite iconsprite = bundle.LoadAsset<Sprite>(itemdata.ItemIcon) as Sprite;                Iconimg.sprite = iconsprite;            }            //设置商品价格            Text price = obj.transform.Find("ButtonBuy").Find("Text_Price").gameObject.GetComponent<Text>();            price.text = itemdata.ItemPrice.ToString();            //设置兑换商品名称            Text nameText = obj.transform.Find("ImageBG").Find("Text_ItemName").gameObject.GetComponent<Text>();            nameText.text = itemdata.ItemName;            //购买按钮点击事件            GameObject Buybtn = obj.transform.Find("ButtonBuy").gameObject;            XPointEvent.AutoAddListener(Buybtn, ClickExchangeItemBtn, itemdata.ItemID);        }        //GameObject recordbtn = ExchangeShopUI.transform.FindChild("ButtonRecord").gameObject;        //XPointEvent.AutoAddListener(recordbtn, ClickExchangeRecordBtn, null);        ShopGoodsInitFlag[(int)SHOPTYPE.SHOPTYPE_EXCHANGE] = true;    }    //刷新兑换商店物品    public void RefreshExchangeShopItem()    {        if (ShopMainUI == null)            return;              //更新兑换按钮显示状态        GameObject tgExchangeBtn = ShopMainUI.transform.Find("Bottom/ToggleGroup/Toggle_Exchange").gameObject;        if (Luancher.IsReviewVersion || ExchangeShopOpenOnOff == false)        {            tgExchangeBtn.SetActive(false);            return;        }        else            tgExchangeBtn.SetActive(true);        if (!ShopGoodsInitFlag[(int)SHOPTYPE.SHOPTYPE_EXCHANGE])            return;        List<uint> exchaneItemList = CCsvDataManager.Instance.ShopItemDataMgr.GetShopItemListByType(SHOPTYPE.SHOPTYPE_EXCHANGE);        if (exchaneItemList.Count == 0)            return;                Transform GoodsShelves = ExchangeShopUI.transform.Find("ImageViewport/Content_Shop_Exchange");        for (int i = 0; i < GoodsShelves.childCount; i++)        {            Object.Destroy(GoodsShelves.GetChild(i).gameObject);        }                byte playervipLv = GameMain.hall_.GetPlayerData().GetVipLevel();        long playerlottery = GameMain.hall_.GetPlayerData().GetLottery();        foreach (uint itemid in exchaneItemList)        {            ShopItemdata itemdata = CCsvDataManager.Instance.ShopItemDataMgr.GetShopItemData(itemid);            if (itemdata == null)                continue;            //实物兑换上架条件            if (itemdata.ItemType == ITEMTYPE.ITEMTYPE_JINGDONG || itemdata.ItemType == ITEMTYPE.ITEMTYPE_MOBILE)            {                //特殊时间段内都显示(22:00 -8:00)                if (System.DateTime.Now.Hour >= 8 && System.DateTime.Now.Hour < 22)                {                    //只有VIP2 且有奖券才显示实物兑换                    if (playervipLv < 2 || playerlottery < 200)                        continue;                }            }            GameObject obj = GameMain.instantiate(ExchangeItemPerfab) as GameObject;            obj.transform.SetParent(GoodsShelves, false);            //设置商品ICON            Image Iconimg = obj.transform.Find("ImageBG").Find("ImageIcon").gameObject.GetComponent<Image>();            AssetBundle bundle = AssetBundleManager.GetAssetBundle(GameDefine.HallAssetbundleName);            if (bundle != null)            {                Sprite iconsprite = bundle.LoadAsset<Sprite>(itemdata.ItemIcon) as Sprite;                Iconimg.sprite = iconsprite;            }            //设置商品价格            Text price = obj.transform.Find("ButtonBuy").Find("Text_Price").gameObject.GetComponent<Text>();            price.text = itemdata.ItemPrice.ToString();            //设置兑换商品名称            Text nameText = obj.transform.Find("ImageBG").Find("Text_ItemName").gameObject.GetComponent<Text>();            nameText.text = itemdata.ItemName;            //购买按钮点击事件            GameObject Buybtn = obj.transform.Find("ButtonBuy").gameObject;            XPointEvent.AutoAddListener(Buybtn, ClickExchangeItemBtn, itemdata.ItemID);        }    }    //商店界面中各按钮鼠标按键事件注册    private void InitShopUIBtnListenter()    {        if (ShopMainUI == null)            return;        GameObject returnBtn = ShopMainUI.transform.Find("Top").Find("ButtonReturn").gameObject;        XPointEvent.AutoAddListener(returnBtn,ClickCloseShopBtn,null);        //GameObject tgCoinBtn = ShopMainUI.transform.FindChild("Bottom").FindChild("ToggleGroup").FindChild("Toggle_Coin").gameObject;        //XPointEvent.AutoAddListener(tgCoinBtn, ClickToggleCoinBtn, null);        GameObject tgDiamondBtn = ShopMainUI.transform.Find("Bottom").Find("Shop_Diamond").gameObject;        XPointEvent.AutoAddListener(tgDiamondBtn, ClickToggleDiamondBtn, null);

