 }#endif#endif        }    }    //提示绑定手机账号回调处理    private void PromptBindMobileCallBack(object parma)    {        int selectState = (int)parma;        if(selectState == 1)        {            //去绑定界面            PlayerInfoUI.Instance.OpenOrClosePlayerInfoUI(true);            PlayerInfoUI.Instance.OpenOrCloseBindPhoneUI(true);        }    }    //点击兑换物品按钮    private void ClickExchangeItemBtn(EventTriggerType eventtype, object parma, PointerEventData eventData)    {        if (eventtype == EventTriggerType.PointerClick)        {            CustomAudio.GetInstance().PlayCustomAudio(1002);
            //GameMain.hall_.GetPlayer().RequestBuyItem((uint)parma);
            uint itemid = (uint)parma;            ShopItemdata itemdata = CCsvDataManager.Instance.ShopItemDataMgr.GetShopItemData(itemid);            if (itemdata == null)                return;            if (GameMain.hall_.GetPlayerData().GetLottery() < itemdata.ItemPrice)            {                CCustomDialog.OpenCustomConfirmUI(1103);                return;            }            switch(itemdata.ItemType)            {                case ITEMTYPE.ITEMTYPE_COIN:                    {                        GameMain.hall_.GetPlayer().RequestBuyItem(itemid);                    }                    break;                case ITEMTYPE.ITEMTYPE_JINGDONG:                    {                        OpenExchangeJingDongUI(itemid);                    }                    break;                case ITEMTYPE.ITEMTYPE_MOBILE:                    {                        OpenExchangeMobileUI(itemid);                    }                    break;            }        }    }    //添加兑换记录    public void AddExchangeRecordData(string orderid,uint itemId,byte orderstate,bool addlist = true)    {        ShopItemdata itemdata = CCsvDataManager.Instance.ShopItemDataMgr.GetShopItemData(itemId);        if (itemdata == null)            return;        if (ExchangeRecordPrefab == null)        {            AssetBundle bundle = AssetBundleManager.GetAssetBundle(GameDefine.HallAssetbundleName);            if (bundle == null)                return;            ExchangeRecordPrefab = bundle.LoadAsset<Object>("Shop_pop_up_Record") as Object;        }        if(addlist)        {            ExchangeRecordData redata = new ExchangeRecordData(orderid, itemId, orderstate);            RecordDataList.Add(redata);        }        GameObject recordobj = GameMain.instantiate(ExchangeRecordPrefab) as GameObject;        Transform prtf = ShopMainUI.transform.Find("pop_up/Exchange_Record/Viewport_Exchange_Record/Content_Exchange_Record");        recordobj.transform.SetParent(prtf, false);        recordobj.transform.Find("TextRiqi").gameObject.GetComponent<Text>().text = orderid;        recordobj.transform.Find("TextJiangpin").gameObject.GetComponent<Text>().text = itemdata.ItemName;        string statetxt = "<color=#FFFF00FF>等待处理</color>";        if (orderstate == 1)            statetxt = "<color=#00FF00FF>已处理</color>";        recordobj.transform.Find("TextZhuangtai").gameObject.GetComponent<Text>().text = statetxt;    }    //刷新兑换记录界面    private void RefreshExchangeRecordDataUI()    {        Transform prtf = ShopMainUI.transform.Find("pop_up/Exchange_Record/Viewport_Exchange_Record/Content_Exchange_Record");        if (prtf.childCount > 0 || RecordDataList.Count == 0)            return;        for(int i =0; i< RecordDataList.Count; i++)        {            ExchangeRecordData redata = RecordDataList[i];            AddExchangeRecordData(redata.ExchangeOrderNo,redata.ExchangeItemId,redata.ExchangeOrderState,false);        }    }    //处理兑换记录数据消息    private bool HandleExchangeRecordDataMsg(uint msgType, UMessage umsg)    {        CCustomDialog.CloseCustomWaitUI();        uint playerid = umsg.ReadUInt();        int recordcount = umsg.ReadInt();        ExChangeRecordDataFlag = true;        for (int i=0; i< recordcount; i++)        {            string orderid = umsg.ReadString();            byte orderstate = umsg.ReadByte();            uint itemid = umsg.ReadUInt();            AddExchangeRecordData(orderid,itemid,orderstate);        }                return true;    }    //处理商城兑换开关消息    private bool HandleExchangeTurnOnOff(uint msgType, UMessage umsg)    {                bool turnoff = umsg.ReadBool();        if (ExchangeShopOpenOnOff != turnoff)        {            ExchangeShopOpenOnOff = turnoff;            RefreshExchangeShopItem();            if (!ExchangeShopOpenOnOff && isShowActive())                ShowKindsShopUI(SHOPTYPE.SHOPTYPE_DIAMOND);        }        return true;    }}