using System.Collections.Generic;using UnityEngine;using UnityEngine.EventSystems;using UnityEngine.UI;using USocket.Messages;using XLua;public struct GiveRecordData{    public uint GiveId;    public byte GiveType;    public long GiveDate;    public long GiveCoinNum;    public string GivePlayerName;    public int Faceid;    public uint GivePlayerId;    public string url;    public GiveRecordData(uint giveid,byte givetype,long givedate,long givecoin,string playername, int faceid, uint giveplayerid)    {        GiveId = giveid;        GiveType = givetype;        GiveDate = givedate;        GiveCoinNum = givecoin;        GivePlayerName = playername;        Faceid = faceid;        GivePlayerId = giveplayerid;        url = "";    }}[Hotfix]public class PlayerInfoUI{    public static PlayerInfoUI Instance = new PlayerInfoUI();    //个人信息界面    private GameObject PlayerInfoMainUI = null;    private bool RegisterChangeNameBtnFlag = false;    private bool RegisterChangeIconBtnFlag = false;    private GameObject PlayerIconToggleOnObj = null;    private GameObject BindCellPhoneNumberUIObj = null;    bool isAskTotalData = false;    //验证码计时器    private CTimerPersecondCall BindMobileCodeTimer;    //赠送记录数据是否已请求    public bool bHaveRequestGiveRecordData;    private Object SingleGiveRecordPrefabObj;    private Transform GiveRecordContentObjTf;    private GameObject CurrentSelectSingleRecordObj;    public List<GiveRecordData> m_GiveRecordDataList;    private bool bNeedResetGiveRecordUIFlag;    PlayerInfoUI()    {        RegisterChangeNameBtnFlag = false;        RegisterChangeIconBtnFlag = false;        bHaveRequestGiveRecordData = false;        bNeedResetGiveRecordUIFlag = true;        PlayerIconToggleOnObj = null;        BindCellPhoneNumberUIObj = null;        SingleGiveRecordPrefabObj = null;        CurrentSelectSingleRecordObj = null;        m_GiveRecordDataList = new List<GiveRecordData>();        InitPlayerI