    {        if (PlayerInfoMainUI == null)            return;        if (GameMain.hall_.GetPlayerData().totalinfo.Count == 0)            return;        Text credit = PlayerInfoMainUI.transform.Find("ImageBG/Middle/Image_xinyu/Text_num").gameObject.GetComponent<Text>();
        credit.text = GameMain.hall_.GetPlayerData().creditScore.ToString();

        for (int index = 1; index < 3; index++)
        {
            GameObject infobg = PlayerInfoMainUI.transform.Find("ImageBG/Middle/GameInfo_" + index.ToString()).gameObject;
            byte key = (index == 1) ? (byte)7 : (byte)13;
            if (!GameMain.hall_.GetPlayerData().totalinfo.ContainsKey(key))
                continue;
            Text matchtimes = infobg.transform.Find("Text_bisaichangci/Text").gameObject.GetComponent<Text>();
            matchtimes.text = GameMain.hall_.GetPlayerData().totalinfo[key].matchtimes.ToString();
            Text finaltimes = infobg.transform.Find("Text_juesai/Text").gameObject.GetComponent<Text>();
            finaltimes.text = GameMain.hall_.GetPlayerData().totalinfo[key].finaltimes.ToString();
            Text bestscore = infobg.transform.Find("Text_zuijia/Text").gameObject.GetComponent<Text>();
            bestscore.text = GameMain.hall_.GetPlayerData().totalinfo[key].bestsort.ToString();
            Text alltimes = infobg.transform.Find("Text_zongshu/Text").gameObject.GetComponent<Text>();
            alltimes.text = GameMain.hall_.GetPlayerData().totalinfo[key].totaltimes.ToString();
            Text info1 = infobg.transform.Find("Text_info_1/Text").gameObject.GetComponent<Text>();
            info1.text = GameMain.hall_.GetPlayerData().totalinfo[key].special1.ToString();
            Text info2 = infobg.transform.Find("Text_info_2/Text").gameObject.GetComponent<Text>();
            info2.text = GameMain.hall_.GetPlayerData().totalinfo[key].special2.ToString();
        }

        SetChangeInfoData((int)GameKind_Enum.GameKind_LandLords, 1);
    }    public void OpenOrClosePlayerInfoUI(bool bShow)    {        if (PlayerInfoMainUI == null && bShow)            InitPlayerInfoUI();        //主动刷新一下玩家数据        if (bShow)        {            InitPlayerDataInfo();            InitPlayerInfoUIBtnEvent();            InitPlayerInfoPanel();        }        PlayerInfoMainUI.SetActive(bShow);        Ask4PlayerTotalData();    }    //关闭角色信息    private void ClickClosePlayerInfoBtn(EventTriggerType eventtype, object parma, PointerEventData eventData)    {        if (eventtype == EventTriggerType.PointerClick)        {            CustomAudio.GetInstance().PlayCustomAudio(1002);            OpenOrClosePlayerInfoUI(false);        }    }    //修改昵称按钮    private void ClickChangeNameBtn(EventTriggerType eventtype, object parma, PointerEventData eventData)    {        if (eventtype == EventTriggerType.PointerClick)        {            CustomAudio.GetInstance().PlayCustomAudio(1002);            ShowTopChildUI(1);        }    }    //更新头像ICON按钮    private void ClickChangeIconBtn(EventTriggerType eventtype, object parma, PointerEventData eventData)    {        if (eventtype == EventTriggerType.PointerClick)        {            CustomAudio.GetInstance().PlayCustomAudio(1002);            ShowTopChildUI(2);        }    }    //更换账号按钮    private void ClickChangeAccountBtn(EventTriggerType eventtype, object parma, PointerEventData eventData)    {        if (eventtype == EventTriggerType.PointerClick)        {            CustomAudio.GetInstance().PlayCustomAudio(1002);
