ect;        popobj.SetActive(bShow);        popobj.transform.Find("PopUp_GiveRecord").gameObject.SetActive(bShow);        //请求记录数据        if(bShow )        {            GiveRecordContentObjTf = popobj.transform.Find("PopUp_GiveRecord").Find("ImageBG")                .Find("Viewport_GiveRecord").Find("Content_GiveRecord");            if (bNeedResetGiveRecordUIFlag)            {                GameObject closebtn = popobj.transform.Find("PopUp_GiveRecord").Find("ButtonClose").gameObject;                XPointEvent.AutoAddListener(closebtn, onClickCloseGiveRecordUIBtn, null);                GameObject clearbtn = popobj.transform.Find("PopUp_GiveRecord").Find("ButtonClear").gameObject;                XPointEvent.AutoAddListener(clearbtn, onClickClearGiveRecordDataBtn, null);                RefreshGiveRecordContentUIObj();                        }            if (!bHaveRequestGiveRecordData)            {                bHaveRequestGiveRecordData = true;                m_GiveRecordDataList.Clear();                RequestGiveRecordData(0);            }            }    }    //点击关闭赠送记录界面按钮    private void onClickCloseGiveRecordUIBtn(EventTriggerType eventtype, object parma, PointerEventData eventData)    {        if (eventtype == EventTriggerType.PointerClick)        {            CustomAudio.GetInstance().PlayCustomAudio(1002);            OpenOrCloseGiveRecordUI(false);        }    }    //点击一键清空赠送记录按钮    private void onClickClearGiveRecordDataBtn(EventTriggerType eventtype, object parma, PointerEventData eventData)    {        if (eventtype == EventTriggerType.PointerClick)        {            CustomAudio.GetInstance().PlayCustomAudio(1002);            if (m_GiveRecordDataList.Count > 0)               CCustomDialog.OpenCustomDialogWithTipsID(1307, CallBackClearGiveRecord);        }    }    //确认清空赠送记录    private void CallBackClearGiveRecord(object pragma)    {        int SelectState = (int)pragma;        //确认清空        if(SelectState == 1)        {            RefreshGiveRecordContentUIObj(true);            RequestDeleteGiveRecord(0);                    }    }    //打开或关闭邀请码界面    private void OpenOrCloseInviteCodeUI(bool bShow)    {        GameObject popobj = PlayerInfoMainUI.transform.Find("ImageBG").Find("Pop_up").gameObject;        popobj.SetActive(bShow);        popobj.transform.Find("PopUp_invitecode").gameObject.SetActive(bShow);        if(bShow)        {            GameObject OkBtn = popobj.transform.Find("PopUp_invitecode").Find("ButtonOk").gameObject;            XPointEvent.AutoAddListener(OkBtn, ClickSetInviteCodeOkBtn, null);            GameObject cancelBtn = popobj.transform.Find("PopUp_invitecode").Find("ButtonCancel").gameObject;            XPointEvent.AutoAddListener(cancelBtn, ClickSetInviteCodeCancelBtn, null);        }    }    //点击设置邀请码确定按钮    private void ClickSetInviteCodeOkBtn(EventTriggerType eventtype, object parma, PointerEventData eventData)    {        if (eventtype == EventTriggerType.PointerClick)        {            CustomAudio.GetInstance().PlayCustomAudio(1002);            Transform invitetf = PlayerInfoMainUI.transform.Find("ImageBG").Find("Pop_up").Find("PopUp_invitecode");            InputField invitecodeInput = invitetf.Find("ImageBG").Find("InputNum").gameObject.GetComponent<InputField>();            string codestr = invitecodeInput.text;            if (string.IsNullOrEmpty(codestr))                return;            //检测是否有效邀请码            if (codestr.Length != 6)                return;            //发送验证码到服务器            SendSetInviteCodeMsg(codestr);            OpenOrCloseInviteCodeUI(false);        }    }    //点击设置邀请码确定按钮    private void ClickSetInviteCodeCancelBtn(EventTriggerType eventtype, object parma, PointerEventData eventData)    {        if (eventtype == EventTriggerType.PointerClick)        {            CustomAudio.GetInstance().PlayCustomAudio(1002);            OpenOrCloseInviteCodeUI(fal