    }    //初始绑定手机号界面    private void InitBindCellPhoneUI()    {        if (PlayerInfoMainUI == null)            return;        AssetBundle bundle = AssetBundleManager.GetAssetBundle(GameDefine.HallAssetbundleName);        if (bundle == null)            return;        Object bindperfab = bundle.LoadAsset<Object>("PopUp_Verify");        BindCellPhoneNumberUIObj = GameMain.instantiate(bindperfab) as GameObject;        BindCellPhoneNumberUIObj.transform.SetParent(PlayerInfoMainUI.transform.Find("ImageBG").Find("Pop_up"),false);        BindCellPhoneNumberUIObj.SetActive(false);        GameObject sendcodeBtn = BindCellPhoneNumberUIObj.transform.Find("PhoneNumbe_Binding")            .Find("ImageBG").Find("ButtonSend").gameObject;        XPointEvent.AutoAddListener(sendcodeBtn, ClickSendVerificationCodeBtn, null);        GameObject okBtn = BindCellPhoneNumberUIObj.transform.Find("PhoneNumbe_Binding")            .Find("ButtonOk").gameObject;        XPointEvent.AutoAddListener(okBtn, ClickBindPhoneOkBtn, null);        GameObject cancelBtn = BindCellPhoneNumberUIObj.transform.Find("PhoneNumbe_Binding")            .Find("ButtonCancel").gameObject;        XPointEvent.AutoAddListener(cancelBtn, ClickBindPhoneCancelBtn, null);    }    //绑定手机号结果处理    public void HandleBindMobileResult(bool bSuccess)    {        if (BindMobileCodeTimer != null)            BindMobileCodeTimer.SetDeleteFlag(true);        if (BindCellPhoneNumberUIObj == null)            return;        GameObject getcodeBtn = BindCellPhoneNumberUIObj.transform.Find("PhoneNumbe_Binding")               .Find("ImageBG").Find("ButtonSend").gameObject;        getcodeBtn.GetComponent<Image>().raycastTarget = true;        getcodeBtn.GetComponent<Button>().interactable = true;        Text strCode = getcodeBtn.transform.Find("Text").gameObject.GetComponent<Text>();        strCode.text = "获取验证码";    }    //点击获取验证码按钮    private void ClickSendVerificationCodeBtn(EventTriggerType eventtype, object parma, PointerEventData eventData)    {        if (eventtype == EventTriggerType.PointerClick)        {            CustomAudio.GetInstance().PlayCustomAudio(1002);            if (BindCellPhoneNumberUIObj == null)                return;            InputField phoneInput = BindCellPhoneNumberUIObj.transform.Find("PhoneNumbe_Binding")            .Find("ImageBG").Find("InputPhoneNum").gameObject.GetComponent<InputField>();            string phonenumberstr = phoneInput.text;            if (string.IsNullOrEmpty(phonenumberstr))            {                CCustomDialog.OpenCustomConfirmUI(1003);                return;            }            //检测是否有效手机号码            if(GameCommon.CheckPhoneIsAble(phonenumberstr))            {                GameObject sendcodeBtn = BindCellPhoneNumberUIObj.transform.Find("PhoneNumbe_Binding")                .Find("ImageBG").Find("ButtonSend").gameObject;                sendcodeBtn.GetComponent<Image>().raycastTarget = false;                sendcodeBtn.GetComponent<Button>().interactable = false;                BindMobileCodeTimer = new CTimerPersecondCall(60f, UpdataBindGetCodeBtnSecondText);                xTimeManger.Instance.RegisterTimer(BindMobileCodeTimer);                //发送手机号码到服务器                CLoginUI.Instance.SendApplyGetCode(long.Parse(phonenumberstr), 2);            }            else            {                CCustomDialog.OpenCustomConfirmUI(1003);            }        }    }    /// <summary>    /// 登陆中的时候 更新 获取验证码按钮上的文字显示    /// </summary>    void UpdataBindGetCodeBtnSecondText(object[] RemainTime)    {        if (BindCellPhoneNumberUIObj == null)            return;        float fRemaintime = (float)RemainTime[0];        GameObject getcodeBtn = BindCellPhoneNumberUIObj.transform.Find("PhoneNumbe_Binding")              .Find("ImageBG").Find("ButtonSend").gameObject;        if (null != getcodeBtn)        {            Text strCode = getcodeBtn.transform.Find("Text").gameObject.GetComponent<Text>();            if (fRemaintime > 1)                strCode.text = fRemaintime.ToString("f0") + "S可重新获取";            else            {                getcodeBtn.GetComponent<Image>().raycastTarget = true;                getcodeBtn.GetComponent<Button>().interactable = true;                strCode.text = "获取验证码";            }        }    }    //点击绑定手机号确定按钮    private void ClickBindPhoneOkBtn(EventTriggerType eventtype, object parma, PointerEventData eventData)    {        if (eventtype == EventTriggerType.PointerClick)        {            CustomAudio.GetInstance().PlayCustomAudio(1002);            if (BindCellPhoneNumberUIObj == null)                return;            InputField codeInput = BindCellPhoneNumberUIObj.transform.Find("PhoneNumbe_Binding")            .Find("ImageBG").Find("InputVerificationCode").gameObject.GetComponent<InputField>();            string codestr = codeInput.text;            if (string.IsNullOrEmpty(codestr))                return;            //检测是否有效验证码            if (codestr.Length != 6)                return;            //发送验证码到服务器            CLoginUI.Instance.SendCodeApplyLogin(uint.Parse(codestr), 2);            OpenOrCloseBindPhoneUI(false);        }    }    //点击绑定手机号取消按钮    private void ClickBindPhoneCancelBtn(EventTriggerType eventtype, object parma, PointerEventData eventData)    {        if (eventtype == EventTriggerType.PointerClick)        {            CustomAudio.GetInstance().PlayCustomAudio(1002);            OpenOrCloseBindPhoneUI(false);        }    }    //注册修改昵称界面按钮    private void RegisterChangeNameUIBtn()    {        if (PlayerInfoMainUI == null)            return;        Transform changnameUItf = PlayerInfoMainUI.transform.Find("ImageBG").Find("Pop_up").Find("PopUp_ChangeName");        GameObject OkBtn = changnameUItf.Find("ButtonOk").gameObject;        XPointEvent.AutoAddListener(OkBtn, ClickChangeNameOkBtn, null);        GameObject cancelBtn = changnameUItf.Find("ButtonCancel").gameObject;        XPointEvent.AutoAddListener(cancelBtn, ClickChangeNameCancelBtn, null);        RegisterChangeNameBtnFlag = true;    }    //注册修改玩家ICON界面按钮    private void RegisterChangeIconUIBtn()    {        if (Luancher.IsVChatLogin)            return;        if (PlayerInfoMainUI == null)            return;        Transform changIconUItf = PlayerInfoMainUI.transform.Find("ImageBG").Find("Pop_up").Find("PopUp_ChangeHead");        GameObject iconOkBtn = changIconUItf.Find("ButtonOk").gameObject;        XPointEvent.AutoAddListener(iconOkBtn, ClickChangeIconOkBtn, null);        GameObject iconcancelBtn = changIconUItf.Find("ButtonCancel").gameObject;        XPointEvent.AutoAddListener(iconcancelBtn, ClickChangeIconCancelBtn, null);        //添加Icon到界面中        Dictionary<string, Sprite> iconlist = GameMain.hall_.sprites_;        uint playerid = GameMain.hall_.GetPlayerId();        AssetBundle bundle = AssetBundleManager.GetAssetBundle(GameDefine.HallAssetbundleName);        if (bundle == null)            return;        Object iconper = bundle.LoadAsset("PlayerInfo_ChangeHead") as Object;        Transform IconContaintf = changIconUItf.Find("ImageBG").Find("Viewport_ChangeHead").Find("Content_ChangeHead");        ToggleGroup icongroup = IconContaintf.gameObject.GetComponent<ToggleGroup>();        foreach (var icon in iconlist)        {            //非当前玩家ICON才添加进去            if (icon.Key.CompareTo(playerid.ToString()) != 0)            {                GameObject IconObj = GameMain.instantiate(iconper) as GameObject;                IconObj.name = icon.Key;                IconObj.transform.SetParent(IconContaintf, false);                IconObj.transform.Find("Background").gameObject.GetComponent<Image>().sprite = icon.Value;                IconObj.GetComponent<Toggle>().group = icongroup;                IconObj.GetComponent<Toggle>().onValueChanged.AddListener(                delegate (bool isOn) { OnIconToggleValueChanged(isOn, IconObj); });            }        }        RegisterChangeIconBtnFlag = true;    }    //Icon Toggle选中ICON改变事件处理    private void OnIconToggleValueChanged(bool ison,GameObject onObj)    {        if (ison)        {            PlayerIconToggleOnObj = onObj;        }    }    //点击修改昵称确定按钮    private void ClickChangeNameOkBtn(EventTriggerType eventtype, object parma, PointerEventData eventData)    {        if (eventtype == EventTriggerType.PointerClick)        {            CustomAudio.GetInstance().PlayCustomAudio(1002);            InputField nameInput = PlayerInfoMainUI.transform.Find("ImageBG").Find("Pop_up").Find("PopUp_ChangeName")                .Find("ImageBG").Find("InputName").gameObject.GetComponent<InputField>();            string newnamestr = nameInput.text;            if (string.IsNullOrEmpty(newnamestr))                return;            //检测是否含空格            if(newnamestr.Contains(" "))            {                CCustomDialog.OpenCustomConfirmUI(1220);                return;            }            int newnamestrlen = System.Text.Encoding.UTF8.GetBytes(newnamestr).Length;            //utf-8编码下一个汉字占3字节,最多允许6个汉字            if (newnamestrlen > 18)            {                //Debug.Log("昵称过长，请重新输入");                CCustomDialog.OpenCustomConfirmUI(1221);                return;            }            //屏蔽字检测通过再发送请求            if (GameCommon.CheckForbiddenWord(ref newnamestr, false))            {                //Debug.Log("昵称不合法,请重新输入");                CCustomDialog.OpenCustomConfirmUI(1220);                return;            }            SendChangeNickNameReq(newnamestr);            CloseTopChildUI(1);        }    }    //修改昵称(头像)成功后刷新显示(收到服务器修改成功消息)    public void RefreshShowPlayerNameIcon()    {        if (PlayerInfoMainUI == null)            return;        PlayerData playerdata = GameMain.hall_.GetPlayerData();        Image icon = PlayerInfoMainUI.transform.Find("ImageBG").Find("Middle").Find("Image_HeadFram").Find("Image_Mask")            .Find("Image_Head").gameObject.GetComponent<Image>();        icon.sprite = GameMain.hall_.GetIcon(playerdata.GetPlayerIconURL(), playerdata.GetPlayerID(), (int)playerdata.PlayerIconId);        Text NameText = PlayerInfoMainUI.transform.Find("ImageBG").Find("Middle").Find("Image_Name").Find("Text")            .gameObject.GetComponent<Text>();        NameText.text = playerdata.GetPlayerName();        if(playerdata.IsChangeName)        {            GameObject changNameBtn = PlayerInfoMainUI.transform.Find("Middle").Find("Image_Name")                                 .Find("ButtonXiugai").gameObject;            changNameBtn.SetActive(false);        }    }    //点击修改昵称取消按钮    private void ClickChangeNameCancelBtn(EventTriggerType eventtype, object parma, PointerEventData eventData)    {        if (eventtype == EventTriggerType.PointerClick)        {            CustomAudio.GetInstance().PlayCustomAudio(1002);            CloseTopChildUI(1);        }    }    //点击修改icon 确定按钮    private void ClickChangeIconOkBtn(EventTriggerType eventtype, object parma, PointerEventData eventData)    {        if (eventtype == EventTriggerType.PointerClick)        {            CustomAudio.GetInstance().PlayCustomAudio(1002);
            if (PlayerIconToggleOnObj != null)            {                if (Luancher.IsVChatLogin)                    return;                Debug.Log("Player Icon:"+PlayerIconToggleOnObj.name);                CloseTopChildUI(2);                //发送请求修改ICON消息                ushort playerIconId = 0;                ushort.TryParse(PlayerIconToggleOnObj.name, out playerIconId);                SendChangeIconIDReq(playerIconId);            }                  }    }    //点击修改icon取消按钮    private void ClickChangeIconCancelBtn(EventTriggerType eventtype, object parma, PointerEventData eventData)    {        if (eventtype == EventTriggerType.PointerClick)        {            CustomAudio.GetInstance().PlayCustomAudio(1002);            CloseTopChildUI(2);        }    }    //显示子功能界面    private void ShowTopChildUI(byte childtype)    {        if (PlayerInfoMainUI == null)            return;        GameObject popobj = PlayerInfoMainUI.transform.Find("ImageBG").Find("Pop_up").gameObject;        switch (childtype)        {            case 1:                {                    if (!RegisterChangeNameBtnFlag)                        RegisterChangeNameUIBtn();                    GameObject chgenameui = popobj.transform.Find("PopUp_ChangeName").gameObject;                    chgenameui.SetActive(true);                    popobj.SetActive(true);                }                break;            case 2:                {                    if (!RegisterChangeIconBtnFlag)                        RegisterChangeIconUIBtn();                    GameObject chgeiconui = popobj.transform.Find("PopUp_ChangeHead").gameObject;                    chgeiconui.SetActive(true);                    popobj.SetActive(true);                }                break;        }    }     //关闭子功能界面    private void CloseTopChildUI(byte childtype)    {        if (PlayerInfoMainUI == null)            return;        GameObject popobj = PlayerInfoMainUI.transform.Find("ImageBG").Find("Pop_up").gameObject;        switch (childtype)        {            case 1:                {                                       GameObject chgenameui = popobj.transform.Find("PopUp_ChangeName").gameObject;                    chgenameui.SetActive(false);                    popobj.SetActive(false);                                    }                break;            case 2:                {                                       GameObject chgeiconui = popobj.transform.Find("PopUp_ChangeHead").gameObject;                    chgeiconui.SetActive(false);                    popobj.SetActive(false);                }                break;        }    }    /// <summary>    /// 发送消息到服务器获取工会赠送数据    /// </summary>    public void RequestGiveRecordData(short nBeginSign)    {        UMessage ApplyMsg = new UMessage((uint)GameCity.EMSG_ENUM.CrazyCityMsg_GETROLECLUBGIVEDATA);        ApplyMsg.Add(nBeginSign);        ApplyMsg.Add(GameMain.hall_.GetPlayerId());        NetWorkClient.GetInstance().SendMsg(ApplyMsg);    }    /// <summary>    /// 添加赠送记录data到list    /// </summary>    /// <param name="giveData"></param>    /// <returns></returns>    private bool AddSingleGiveRecordDataToList(GiveRecordData giveData)    {        //先判断下列表里是否已经存在        for (int i = 0; i < m_GiveRecordDataList.Count; i++)        {            if (m_GiveRecordDataList[i].GiveId == giveData.GiveId)                return false;        }        m_GiveRecordDataList.Add(giveData);        AddSingleGiveRecordToUI(giveData);        return true;    }    /// <summary>    /// 添加一条赠送记录到界面    /// </summary>    /// <param name="timedate"></param>    /// <param name="playername"></param>    /// <param name="coinNum"></param>    /// <param name="giveType"></param>    /// <returns></returns>    private bool AddSingleGiveRecordToUI(GiveRecordData giveData)    {        if (SingleGiveRecordPrefabObj == null)            return false;        GameObject giveobj = GameMain.instantiate(SingleGiveRecordPrefabObj) as GameObject;        giveobj.transform.SetParent(GiveRecordContentObjTf, false);        giveobj.transform.SetAsFirstSibling();        giveobj.name = giveData.GiveId.ToString();        string datetxt = GameCommon.ConvertLongToDateTime(giveData.GiveDate).ToString("yyyy年MM月dd日HH:mm:ss");        if (giveData.GiveType == 1)            datetxt += "您资助<color=#E2FF2D>" + giveData.GivePlayerName + "</color>金币<color=#FF0000>" + giveData.GiveCoinNum.ToString() + "</color>";        else            datetxt += "<color=#E2FF2D>" + giveData.GivePlayerName + "</color>资助您金币<color=#00FF02>" + giveData.GiveCoinNum.ToString() + "</color>";        giveobj.transform.Find("TextDate").gameObject.GetComponent<Text>().text = datetxt;        //giveobj.transform.FindChild("TextName").gameObject.GetComponent<Text>().text =giveData.GivePlayerName;                //string givetxt = "<color=#FF0000>-"+ giveData.GiveCoinNum.ToString() + "</color>";        //if (giveData.GiveType == 2)        //    givetxt = "<color=#00FF02>+" + giveData.GiveCoinNum.ToString() + "</color>";        //giveobj.transform.FindChild("TextNum").gameObject.GetComponent<Text>().text = givetxt;        XPointEvent.AutoAddListener(giveobj,OnClickSingleGiveRecord, giveData.GiveId);        GameObject deletebtn =  giveobj.transform.Find("ButtonDelete").gameObject;        deletebtn.SetActive(false);        XPointEvent.AutoAddListener(deletebtn,OnClickDeleteSingleGiveRecord, giveData.GiveId);        return true;    }    /// <summary>    /// 刷新界面或清空赠送记录    /// </summary>    /// <param name="isClear"></param>    private void RefreshGiveRecordContentUIObj(bool isClear = false)    {        for (int i = 0; i < GiveRecordContentObjTf.childCount; i++)        {            Object.Destroy(GiveRecordContentObjTf.GetChild(i).gameObject);        }        if(isClear)        {            m_GiveRecordDataList.Clear();        }        if(m_GiveRecordDataList.Count >0 && !isClear)        {            for (int i = 0; i < m_GiveRecordDataList.Count; i++)            {                AddSingleGiveRecordToUI(m_GiveRecordDataList[i]);            }        }        bNeedResetGiveRecordUIFlag = false;    }    /// <summary>    /// 选中单条记录    /// </summary>    /// <param name="eventtype"></param>    /// <param name="parma"></param>    /// <param name="eventData"></param>    private void OnClickSingleGiveRecord(EventTriggerType eventtype, object parma, PointerEventData eventData)    {        if(eventtype == EventTriggerType.PointerClick)        {
            CustomAudio.GetInstance().PlayCustomAudio(1002);            string giveidstr = parma.ToString();            //如果当前已经被选中            if (CurrentSelectSingleRecordObj != null)            {                if (CurrentSelectSingleRecordObj.name.CompareTo(giveidstr) == 0)                    return;            }            GameObject btnobj = GiveRecordContentObjTf.transform.Find(giveidstr).Find("ButtonDelete").gameObject;            btnobj.SetActive(true);            if (CurrentSelectSingleRecordObj != null)                CurrentSelectSingleRecordObj.transform.Find("ButtonDelete").gameObject.SetActive(false);            CurrentSelectSingleRecordObj = GiveRecordContentObjTf.transform.Find(giveidstr).gameObject;        }    }    /// <summary>    /// </summary>    /// <param name="eventtype"></param>    /// <param name="parma"></param>    /// <param name="eventData"></param>    private void OnClickDeleteSingleGiveRecord(EventTriggerType eventtype, object parma, PointerEventData eventData)    {        if (eventtype == EventTriggerType.PointerClick)        {            CustomAudio.GetInstance().PlayCustomAudio(1002);            uint giveId = (uint)parma;            GameObject obj = GiveRecordContentObjTf.transform.Find(giveId.ToString()).gameObject;            GameMain.safeDeleteObj(obj);            for(int i = 0; i<m_GiveRecordDataList.Count;i++)            {                if (m_GiveRecordDataList[i].GiveId == giveId)                {                    m_GiveRecordDataList.RemoveAt(i);                    break;                }                     }            CurrentSelectSingleRecordObj = null;            RequestDeleteGiveRecord(giveId);        }    }    /// <summary>    /// 服务器通知客户端工会赠送数据    /// </summary>    private  bool RecvRoleGiveRecordData(uint _msgType, UMessage _ms)    {        short nBeginSign = _ms.ReadShort();        if (nBeginSign == -2)    //没有数据        {            Debug.Log("This player has not club give data!");        }        else if(nBeginSign == -3)   //添加        {            short nSize = _ms.ReadShort();            GiveRecordData GRData = new GiveRecordData();            GRData.GiveId = _ms.ReadUInt();            GRData.GiveType = _ms.ReadByte();    // 1赠送 2接受            GRData.GivePlayerId = _ms.ReadUInt();            GRData.GiveDate = _ms.ReadLong();            GRData.GiveCoinNum = _ms.ReadLong();            GRData.GivePlayerName = _ms.ReadString();            GRData.Faceid = _ms.ReadInt();            AddSingleGiveRecordDataToList(GRData);                      Debug.Log("Add new GiveRecord:" +                "nSign: " + GRData.GiveId.ToString() + "name:" + GRData.GivePlayerName +                "coin: " + GRData.GiveCoinNum);        }        else        {            short nSize = _ms.ReadShort();            for (int i = 0; i < nSize; i++)            {                GiveRecordData GRData = new GiveRecordData();                GRData.GiveId = _ms.ReadUInt();                GRData.GiveType = _ms.ReadByte();    // 1赠送 2接受                GRData.GivePlayerId = _ms.ReadUInt();                GRData.GiveDate = _ms.ReadLong();                GRData.GiveCoinNum = _ms.ReadLong();                GRData.GivePlayerName = _ms.ReadString();                GRData.Faceid = _ms.ReadInt();                AddSingleGiveRecordDataToList(GRData);                Debug.Log("Old GiveRecord:" +                    "nSign: " + GRData.GiveId.ToString() + "name:" +GRData.GivePlayerName +                    "coin: " + GRData.GiveCoinNum);            }            if (nBeginSign == -1)            {                Debug.Log("Get role club give data over!");            }            else            {                nBeginSign += nSize;                RequestGiveRecordData(nBeginSign);            }        }        return true;    }    /// <summary>    /// 移除工会赠送记录    /// </summary>    public void RequestDeleteGiveRecord(uint nSign)    {        UMessage ApplyMsg = new UMessage((uint)GameCity.EMSG_ENUM.CrazyCityMsg_PLAYERREMOVECLUBGIVEDATA);        ApplyMsg.Add(nSign);        ApplyMsg.Add(GameMain.hall_.GetPlayerId());        NetWorkClient.GetInstance().SendMsg(ApplyMsg);    }    //场景切换后重新填充界面数据标记    public void ResetInitIconFlag()    {        RegisterChangeIconBtnFlag = false;        RegisterChangeNameBtnFlag = false;        bNeedResetGiveRecordUIFlag = true;    }    /// <summary>    /// 切换账号后重新请求数据标记    /// </summary>    public void ChangeAccountResetDateFlag()    {        bHaveRequestGiveRecordData = false;        bNeedResetGiveRecordUIFlag = true;    }    /// <summary>    /// 请求修改昵称    /// </summary>    /// <param name="name">新的昵称名</param>    public void SendChangeNickNameReq(string name)    {        UMessage msgChangeNickName = new UMessage((uint)GameCity.EMSG_ENUM.CrazyCityMsg_CHANGENICKNAME);        MessageChangeNickName mcnn_ = new MessageChangeNickName();        mcnn_.uid = GameMain.hall_.GetPlayerId();        mcnn_.name = name;        mcnn_.faceid = 0;        mcnn_.SetSendData(msgChangeNickName);        NetWorkClient.GetInstance().SendMsg(msgChangeNickName);    }    /// <summary>    /// 请求修改头像    /// </summary>    /// <param name="name">新的昵称名</param>    public void SendChangeIconIDReq(ushort nfaceid)    {        UMessage msgChangeNickName = new UMessage((uint)GameCity.EMSG_ENUM.CrazyCityMsg_CHANGENICKNAME);        MessageChangeNickName mcnn_ = new MessageChangeNickName();        mcnn_.uid = GameMain.hall_.GetPlayerId();        mcnn_.name = "";        mcnn_.faceid = nfaceid;        mcnn_.SetSendData(msgChangeNickName);        NetWorkClient.GetInstance().SendMsg(msgChangeNickName);    }    /// <summary>    /// 修改昵称成功处理    /// </summary>    /// <param name="_msgType"></param>    /// <param name="_ms"></param>    /// <returns></returns>    bool ChangeNickNameSuccess(uint _msgType, UMessage _ms)    {        PlayerData playerdata = GameMain.hall_.GetPlayerData();        byte isSuccess = _ms.ReadByte();        if (isSuccess == 1)  //修改昵称        {            playerdata.SetPlayerName(_ms.ReadString());            playerdata.IsChangeName = true;            Debug.Log("修改昵称成功：" + playerdata.GetPlayerName());        }        else if (isSuccess == 2) //修改头像        {            playerdata.PlayerIconId = _ms.ReadUInt();            Debug.Log("修改头像成功!头像id：" + playerdata.PlayerIconId.ToString());        }        else if (isSuccess == 3) //都修改了        {            playerdata.SetPlayerName (_ms.ReadString());            playerdata.IsChangeName = true;            playerdata.PlayerIconId = _ms.ReadUInt();            Debug.Log("修改昵称成功：" + playerdata.GetPlayerName());            Debug.Log("修改头像成功!头像id：" + playerdata.PlayerIconId.ToString());        }        else if (isSuccess == 4) //昵称修改失败        {            Debug.Log("修改昵称失败");            CCustomDialog.OpenCustomConfirmUI(1301);        }        else if (isSuccess == 5) ////5修改昵称失败但修改头像成功        {            playerdata.PlayerIconId = _ms.ReadUInt();            Debug.Log("修改昵称失败但修改头像成功!头像id：" + playerdata.PlayerIconId.ToString());        }        GameMain.hall_.RefreshPlayerNameIcon();        RefreshShowPlayerNameIcon();        return true;    }    /// <summary>    /// 发送设置验证码消息    /// </summary>    /// <param name="InviteCodestr"></param>    private void SendSetInviteCodeMsg(string InviteCodestr)    {        //UMessage msginvitecode = new UMessage((uint)GameCity.EMSG_ENUM.CCMsg_CM_PLAYERAPPLYBINDINVITE);        //msginvitecode.Add(GameMain.hall_.GetPlayerId());        //msginvitecode.Add(InviteCodestr);        //NetWorkClient.GetInstance().SendMsg(msginvitecode);    }}